import React, { useState, useEffect } from 'react';
import RecipeForm from '../components/RecipeForm';
import RecipeCard from '../components/RecipeCard';
import { getRecipes, addRecipe, updateRecipe, deleteRecipe } from '../services/api';

const HomePage = () => {
  const [recipes, setRecipes] = useState([]);
  const [editingRecipe, setEditingRecipe] = useState(null);

  useEffect(() => {
    fetchRecipes();
  }, []);

  const fetchRecipes = async () => {
    const data = await getRecipes();
    setRecipes(data);
  };

  const handleAddRecipe = async (newRecipe) => {
    await addRecipe(newRecipe);
    fetchRecipes();
  };

  const handleEditRecipe = async (updatedRecipe) => {
    await updateRecipe(editingRecipe._id, updatedRecipe);
    setEditingRecipe(null);
    fetchRecipes();
  };

  const handleDeleteRecipe = async (id) => {
    await deleteRecipe(id);
    fetchRecipes();
  };

  return (
    
    <div className="container">
      <h1 className="text-center mt-4">Cookbook</h1>
      <RecipeForm onSubmit={editingRecipe ? handleEditRecipe : handleAddRecipe} initialData={editingRecipe} />
      <div className="row mt-4">
        {recipes.map((recipe) => (
          <RecipeCard
            key={recipe._id}
            recipe={recipe}
            onEdit={(recipe) => setEditingRecipe(recipe)}
            onDelete={handleDeleteRecipe}
          />
        ))}
      </div>
    </div>
  );
};

export default HomePage;
