import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import RecipeForm from '../components/RecipeForm';
import { addRecipe, updateRecipe } from '../services/api';
import "../styles.css";

const HomePage = () => {
  const [editingRecipeState, setEditingRecipe] = useState(null);
  const [isEditingMode, setIsEditingMode] = useState(true); // Flag for editing mode (default true to avoid premature prompts)
  const location = useLocation();
  const navigate = useNavigate();

  const isEditing = Boolean(location.state?.recipe);

  useEffect(() => {
    if (isEditing) {
      setEditingRecipe(location.state.recipe);
      setIsEditingMode(false); // Enable editing mode with unsaved changes
    } else {
      setEditingRecipe(null);
      setIsEditingMode(true); // Disable editing mode (no unsaved changes)
    }
  }, [location.state]);

  // Handle browser navigation (back/forward, refresh)
  useEffect(() => {
    const handleBeforeUnload = (event) => {
      if (!isEditingMode) {
        event.preventDefault();
        event.returnValue = "You have unsaved changes. Are you sure you want to leave?";
      }
    };

    window.addEventListener("beforeunload", handleBeforeUnload);
    return () => {
      window.removeEventListener("beforeunload", handleBeforeUnload);
    };
  }, [isEditingMode]);

  // Handle in-app navigation
  const handleNavigation = (path) => {
    if (!isEditingMode) {
      const confirmLeave = window.confirm("You have unsaved changes. Are you sure you want to leave this page?");
      if (!confirmLeave) return; // Stay on the page
    }
    setIsEditingMode(true); // Reset flag when navigating
    navigate(path);
  };

  const handleAddRecipe = async (newRecipe) => {
    try {
      await addRecipe(newRecipe);
      console.log("Recipe added successfully.");
      setEditingRecipe(null);
      setIsEditingMode(true); // Changes saved
      navigate('/recipes'); // Redirect to Recipes Page
    } catch (error) {
      console.error("Error adding recipe:", error);
    }
  };

  const handleEditRecipe = async (updatedRecipe) => {
    if (!editingRecipeState?._id) {
      console.error("No recipe selected for editing.");
      return;
    }

    try {
      console.log(`Updating recipe with id: ${editingRecipeState._id}`);
      await updateRecipe(editingRecipeState._id, updatedRecipe);
      setEditingRecipe(null);
      setIsEditingMode(true); // Changes saved
      navigate('/recipes'); // Redirect to Recipes Page
      console.log(`Recipe with id ${editingRecipeState._id} successfully updated.`);
    } catch (error) {
      console.error("Error editing recipe:", error);
    }
  };

  const handleFormSubmit = (recipe) => {
    if (editingRecipeState) {
      handleEditRecipe(recipe);
    } else {
      handleAddRecipe(recipe);
    }
  };

  const handleFormReset = () => {
    setEditingRecipe(null);
    setIsEditingMode(true); // No unsaved changes after reset
    navigate('/', { state: null });
  };

  return (
    <>
      {/* Header Section */}
      <header className="text-white py-3">
        <div className="container">
          <div className="row align-items-center justify-content-center">
            <div className="col-12 text-center">
              <h1 className="mb-0">Cookbook</h1>
            </div>
          </div>
          <div className="row align-items-center justify-content-center mt-3">
            <nav>
              <a
                href="#"
                className="text-white mx-2 text-decoration-none"
                onClick={() => handleNavigation('/')}
              >
                Home
              </a>
              <a
                href="#"
                className="text-white mx-2 text-decoration-none"
                onClick={() => handleNavigation('/recipes')}
              >
                Recipes
              </a>
            </nav>
          </div>
        </div>
      </header>

      {/* Main Content Section */}
      <div className="container mt-4" id="body">
        <div className="row justify-content-center">
          <div className="col-md-6">
            <RecipeForm
              onSubmit={handleFormSubmit}
              initialData={editingRecipeState}
              onReset={() => {
                setEditingRecipe(null);
                setIsEditingMode(true); // No unsaved changes after reset
              }}
              onChange={() => setIsEditingMode(false)} // Mark unsaved changes when form is modified
            />
          </div>
        </div>
      </div>

      <footer className="text-white py-3">
        <div className="container">
          <div className="row align-items-center justify-content-center">
            <div className="col-12 text-center">
              <h1 className="mb-0">Cookbook</h1>
            </div>
          </div>
        </div>
      </footer>
    </>
  );
};

export default HomePage;
