import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import RecipeForm from '../components/RecipeForm';
import { addRecipe, updateRecipe } from '../services/api';
import "../styles.css";

const HomePage = () => {
  const [editingRecipeState, setEditingRecipe] = useState(null);
  const [isFormDirty, setIsFormDirty] = useState(false); // Track if form has unsaved changes
  const location = useLocation();
  const navigate = useNavigate();

  // Populate editingRecipeState based on location state
  useEffect(() => {
    if (location.state?.recipe) {
      setEditingRecipe(location.state.recipe);
    } else {
      setEditingRecipe(null);
    }
  }, [location.state]);

  // Warn user if they attempt to leave with unsaved changes
  useEffect(() => {
    const handleBeforeUnload = (e) => {
      if (isFormDirty) {
        e.preventDefault();
        e.returnValue = ''; // Required for displaying the browser's default confirmation dialog
      }
    };

    window.addEventListener('beforeunload', handleBeforeUnload);
    return () => {
      window.removeEventListener('beforeunload', handleBeforeUnload);
    };
  }, [isFormDirty]);

  const handleAddRecipe = async (newRecipe) => {
    try {
      await addRecipe(newRecipe);
      console.log("Recipe added successfully.");
      setEditingRecipe(null);
      setIsFormDirty(false); // Reset the dirty flag
      navigate('/recipes'); // Redirect to Recipes Page
    } catch (error) {
      console.error("Error adding recipe:", error);
    }
  };

  const handleEditRecipe = async (updatedRecipe) => {
    if (!editingRecipeState?._id) {
      console.error("No recipe selected for editing.");
      return;
    }

    try {
      console.log(`Updating recipe with id: ${editingRecipeState._id}`);
      await updateRecipe(editingRecipeState._id, updatedRecipe);
      setEditingRecipe(null);
      setIsFormDirty(false); // Reset the dirty flag
      navigate('/recipes'); // Redirect to Recipes Page
      console.log(`Recipe with id ${editingRecipeState._id} successfully updated.`);
    } catch (error) {
      console.error("Error editing recipe:", error);
    }
  };

  const handleFormReset = () => {
    setEditingRecipe(null);
    setIsFormDirty(false); // Reset the dirty flag
    navigate('/', { state: null });
  };

  return (
    <>
      <header className="text-white py-3">
        <div className="container">
          <div className="row align-items-center justify-content-center">
            <div className="col-12 text-center">
              <h1 className="mb-0">Cookbook</h1>
            </div>
          </div>
          <div className="row align-items-center justify-content-center mt-3">
            <nav>
              <a href="/" className="text-white mx-2 text-decoration-none">Home</a>
              <a href="/recipes" className="text-white mx-2 text-decoration-none">Recipes</a>
            </nav>
          </div>
        </div>
      </header>

      <div className="container mt-4" id="body">
        <div className="row justify-content-center">
          <div className="col-md-6">
            <RecipeForm
              onSubmit={editingRecipeState ? handleEditRecipe : handleAddRecipe}
              initialData={editingRecipeState}
              onReset={handleFormReset}
              setIsFormDirty={setIsFormDirty} // Pass the dirty flag setter to the form
            />
          </div>
        </div>
      </div>

      <footer className="text-white py-3">
        <div className="container">
          <div className="row align-items-center justify-content-center">
            <div className="col-12 text-center">
              <h1 className="mb-0">Cookbook</h1>
            </div>
          </div>
        </div>
      </footer>
    </>
  );
};

export default HomePage;
