import React, { useEffect, useState } from 'react';
import { getRecipes, deleteRecipe } from '../services/api';
import RecipeCard from '../components/RecipeCard';
import { useNavigate } from 'react-router-dom';

const RecipesPage = () => {
  const [recipes, setRecipes] = useState([]);
  const [filteredRecipes, setFilteredRecipes] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('All');

  const navigate = useNavigate();

  useEffect(() => {
    fetchRecipes();
  }, []);

  useEffect(() => {
    filterRecipes();
  }, [searchQuery, selectedCategory, recipes]);

  // Fetch recipes from the backend
  const fetchRecipes = async () => {
    try {
      const data = await getRecipes();
      console.log('Fetched recipes:', data);
      setRecipes(data);
      setFilteredRecipes(data);
    } catch (error) {
      console.error('Error fetching recipes:', error);
    }
  };

  const handleDeleteRecipe = async (id) => {
    try {
      console.log(`Attempting to delete recipe with id: ${id}`);
      await deleteRecipe(id); // Call the delete API
      console.log(`Successfully deleted recipe with id: ${id}`);
      // Remove the deleted recipe from local state
      setRecipes((prevRecipes) => prevRecipes.filter((recipe) => recipe._id !== id));
      console.log('Recipe list successfully updated.');
    } catch (error) {
      console.error('Error deleting recipe:', error);
    }
  };

  const handleEditRecipe = (recipe) => {
    navigate('/', { state: { recipe } });
  };

  // Filter recipes based on search query and category
  const filterRecipes = () => {
    let filtered = recipes;

    // Filter by search query
    if (searchQuery) {
      const lowerCaseQuery = searchQuery.toLowerCase();
      filtered = filtered.filter(
        (recipe) =>
          recipe.title.toLowerCase().includes(lowerCaseQuery) ||
          recipe.category.toLowerCase().includes(lowerCaseQuery) ||
          recipe.ingredients.toLowerCase().includes(lowerCaseQuery)
      );
    }

    // Filter by category
    if (selectedCategory !== 'All') {
      filtered = filtered.filter(
        (recipe) => recipe.category.toLowerCase() === selectedCategory.toLowerCase()
      );
    }

    setFilteredRecipes(filtered);
  };

  return (
    <>
    <header className="text-white py-3">
        <div className="container">
          <div className="row align-items-center justify-content-center">
            <div className="col-12 text-center">
              <h1 className="mb-0">Cookbook</h1>
            </div>
          </div>
          <div className="row align-items-center justify-content-center mt-3">
            <nav>
              <a
                href="/"
                className="text-white mx-2 text-decoration-none">
                Home
              </a>
              <a
                href="#"
                className="text-white mx-2 text-decoration-none"
                onClick={() => handleNavigation('/recipes')}
              >
                Recipes
              </a>
            </nav>
          </div>
        </div>
      </header>

    <div className="container mt-4">
      <h1 className="text-center mb-4">All Recipes</h1>

      {/* Search and Filter Section */}
      <div className="row mb-4">
        {/* Search Bar */}
        <div className="col-md-8 mb-3 mb-md-0">
          <input
            type="text"
            className="form-control"
            placeholder="Search recipes by title, category, or ingredients"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
        </div>

        {/* Dropdown Filter */}
        <div className="col-md-4">
          <select
            className="form-select"
            value={selectedCategory}
            onChange={(e) => setSelectedCategory(e.target.value)}
          >
            <option value="All">All Categories</option>
            <option value="Breakfast">Breakfast</option>
            <option value="Lunch">Lunch</option>
            <option value="Dinner">Dinner</option>
            <option value="Dessert">Dessert</option>
          </select>
        </div>
      </div>

      {/* Recipes Grid */}
      <div className="row">
        {filteredRecipes.length === 0 ? (
          <p className="text-center">No recipes found. Try adjusting your search or filters.</p>
        ) : (
          filteredRecipes.map((recipe) => (
            <RecipeCard
              key={recipe._id}
              recipe={recipe}
              onEdit={() => handleEditRecipe(recipe)}
              onDelete={() => handleDeleteRecipe(recipe._id)}
            />
          ))
        )}
      </div>
    </div>
    </>
  );
};

export default RecipesPage;
