import React, { useEffect, useState } from 'react';
import RecipeForm from '../components/RecipeForm';
import { getRecipes, addRecipe, updateRecipe, deleteRecipe } from '../services/api';
import "../styles.css";
import { useLocation } from 'react-router-dom';

const HomePage = () => {
  const [recipes, setRecipes] = useState([]);
  const [editingRecipe, setEditingRecipe] = useState(null);

  const location = useLocation();

  // Set editingRecipe from navigation state or reset when the page loads
  useEffect(() => {
    if (location.state?.recipe) {
      setEditingRecipe(location.state.recipe);
    } else {
      setEditingRecipe(null); // Clear the form when no recipe is being edited
    }
  }, [location.state]);

  const fetchRecipes = async () => {
    const data = await getRecipes();
    setRecipes(data);
  };

  const handleAddRecipe = async (newRecipe) => {
    await addRecipe(newRecipe);
    fetchRecipes();
  };

  const handleEditRecipe = async (updatedRecipe) => {
    await updateRecipe(editingRecipe._id, updatedRecipe);
    setEditingRecipe(null); // Clear the form after updating
    fetchRecipes();
  };

  const handleDeleteRecipe = async (id) => {
    await deleteRecipe(id);
    fetchRecipes();
  };

  return (
    <>
      <header className="text-white py-3">
        <div className="container">
          <div className="row align-items-center justify-content-center">
            <div className="col-12 text-center">
              <h1 className="mb-0">Cookbook</h1>
            </div>
          </div>
          <div className="row align-items-center justify-content-center mt-3">
            <nav>
              <a href="/" className="text-white mx-2 text-decoration-none">Home</a>
              <a href="/recipes" className="text-white mx-2 text-decoration-none">Recipes</a>
            </nav>
          </div>
        </div>
      </header>

      <div className="container mt-4" id="body">
        <div className="row justify-content-center">
          <div className="col-md-6">
            <RecipeForm 
              onSubmit={editingRecipe ? handleEditRecipe : handleAddRecipe} 
              initialData={editingRecipe} 
            />
          </div>
        </div>
      </div>

      <footer className="text-white py-3">
        <div className="container">
          <div className="row align-items-center justify-content-center">
            <div className="col-12 text-center">
            <p>Cookbook App © 2024 | Created by Your Name</p>
    <p>
      <a href="mailto:contact@cookbookapp.com" className="text-white text-decoration-none">
        Email Us
      </a>
      {' | '}
      <a href="https://github.com/YourGitHubProfile" className="text-white text-decoration-none">
        GitHub
      </a>
    </p>
            </div>
          </div>
        </div>
      </footer>
    </>
  );
};

export default HomePage;
