import React, { useState, useEffect } from 'react';

const RecipeForm = ({ onSubmit, initialData }) => {
  const [recipe, setRecipe] = useState({
    title: '',
    ingredients: '',
    steps: '',
    category: '',
    prepTime: '',
  });

  useEffect(() => {
    if (initialData) setRecipe(initialData);
  }, [initialData]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setRecipe({ ...recipe, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(recipe);
    setRecipe({ title: '', ingredients: '', steps: '', category: '', prepTime: '' });
  };

  return (
    <form onSubmit={handleSubmit}>
      <input name="title" placeholder="Title" value={recipe.title} onChange={handleChange} required />
      <input name="category" placeholder="Category" value={recipe.category} onChange={handleChange} required />
      <input name="prepTime" placeholder="Prep Time" value={recipe.prepTime} onChange={handleChange} />
      <textarea name="ingredients" placeholder="Ingredients" value={recipe.ingredients} onChange={handleChange} required />
      <textarea name="steps" placeholder="Steps" value={recipe.steps} onChange={handleChange} required />
      <button type="submit">Save</button>
    </form>
  );
};

export default RecipeForm;
