import React, { useState, useEffect } from 'react';
import RecipeForm from '../components/RecipeForm';
import RecipeCard from '../components/RecipeCard';
import { getRecipes, addRecipe, updateRecipe, deleteRecipe } from '../services/api';
import "../styles.css"

const HomePage = () => {
  const [recipes, setRecipes] = useState([]);
  const [editingRecipe, setEditingRecipe] = useState(null);

  useEffect(() => {
    fetchRecipes();
  }, []);

  const fetchRecipes = async () => {
    const data = await getRecipes();
    setRecipes(data);
  };

  const handleAddRecipe = async (newRecipe) => {
    await addRecipe(newRecipe);
    fetchRecipes();
  };

  const handleEditRecipe = async (updatedRecipe) => {
    await updateRecipe(editingRecipe._id, updatedRecipe);
    setEditingRecipe(null);
    fetchRecipes();
  };

  const handleDeleteRecipe = async (id) => {
    await deleteRecipe(id);
    fetchRecipes();
  };

  return (
    <>
    <header className="bg-primary text-white py-3">
  <div className="container">
    <div className="row align-items-center justify-content-center">
      {/* Title */}
      <div className="col-12 text-center">
        <h1 className="mb-0">Cookbook</h1>
      </div>
    </div>
    <div className="row align-items-center justify-content-center mt-3">
      {/* Navigation (Optional) */}
      <nav>
        <a href="/" className="text-white mx-2 text-decoration-none">Home</a>
        <a href="/recipes" className="text-white mx-2 text-decoration-none">Recipes</a>
        <a href="/about" className="text-white mx-2 text-decoration-none">About</a>
      </nav>
    </div>
  </div>
</header>
    <div className="container">
      <RecipeForm onSubmit={editingRecipe ? handleEditRecipe : handleAddRecipe} initialData={editingRecipe} />
      <div className="row mt-4" >
        {recipes.map((recipe) => (
          <RecipeCard
            key={recipe._id}
            recipe={recipe}
            onEdit={(recipe) => setEditingRecipe(recipe)}
            onDelete={handleDeleteRecipe}
          />
        ))}
      </div>
    </div>
    </>
  );
};

export default HomePage;
