import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import RecipeForm from '../components/RecipeForm';
import { getRecipes, addRecipe, updateRecipe } from '../services/api';
import "../styles.css"

const HomePage = () => {
  const [recipes, setRecipes] = useState([]);
  const [editingRecipeState, setEditingRecipe] = useState(null);
  const location = useLocation();
  const navigate = use
  const editingRecipe = location.state?.recipe || null; 

  useEffect(() => {
    if (editingRecipe) {
      setEditingRecipe(editingRecipe); // Update state if recipe is passed
    }
  }, [editingRecipe]);

  const fetchRecipes = async () => {
    const data = await getRecipes();
    setRecipes(data);
  };

  const handleAddRecipe = async (newRecipe) => {
    try{
      const addedRecipe = await addRecipe(newRecipe);
      setRecipes((prevRecipes) => [...prevRecipes, addedRecipe]);
    }catch (error){
      console.error("Error adding recipe:", error);
    }
  };

  const handleEditRecipe = async (updatedRecipe) => {
    if (!editingRecipe?._id) {
      console.error("No recipe selected for editing.");
      return;
    }
  
    try {
      console.log(`Updating recipe with id: ${editingRecipe._id}`);
      
      // Call the API to update the recipe
      const editedRecipe = await updateRecipe(editingRecipe._id, updatedRecipe);
      
      // Update the recipes state
      setRecipes((prevRecipes) =>
        prevRecipes.map((recipe) =>
          recipe._id === editingRecipe._id ? editedRecipe : recipe
        )
      );
  
      // Clear the editing recipe state
      setEditingRecipe(null);
      console.log(`Recipe with id ${editingRecipe._id} successfully updated.`);
    } catch (error) {
      console.error("Error editing recipe:", error);
    }
  };

  const handleFormReset = () => {
    setEditingRecipe(null); // Clear the editing recipe state
    navigate('/', { state: null }); // Clear the location state
  };

  return (
    <>
     {/* Header Section */}
     <header className="text-white py-3">
      <div className="container">
        <div className="row align-items-center justify-content-center">
          {/* Title */}
          <div className="col-12 text-center">
            <h1 className="mb-0">Cookbook</h1>
          </div>
        </div>
        <div className="row align-items-center justify-content-center mt-3">
          {/* Navigation */}
          <nav>
            <a href="/" className="text-white mx-2 text-decoration-none">Home</a>
            <a href="/recipes" className="text-white mx-2 text-decoration-none">Recipes</a>
          </nav>
        </div>
      </div>
    </header>

    {/* Main Content Section */}
    <div className="container mt-4" id="body">
    <div className="row justify-content-center">
        <div className="col-md-6">
    <RecipeForm onSubmit={editingRecipeState ? handleEditRecipe : handleAddRecipe} 
    initialData={editingRecipeState} 
    onReset={handleFormReset}
    />
    </div>
    </div>
    </div>

    <footer className="text-white py-3">
      <div className="container">
        <div className="row align-items-center justify-content-center">
        <div className="col-12 text-center">
            <h1 className="mb-0">Cookbook</h1>
          </div>
        </div>
      </div>
    </footer>
  </>
  );
};

export default HomePage;
