import React, { useEffect, useState } from 'react';
import { getRecipes } from '../services/api';
import RecipeCard from '../components/RecipeCard';

const RecipesPage = () => {
  const [recipes, setRecipes] = useState([]);

  useEffect(() => {
    fetchRecipes();
  }, []);

  const fetchRecipes = async () => {
    const data = await getRecipes(); // Fetch recipes from the backend
    setRecipes(data); // Store them in the state
  };

  return (
    <div className="container mt-4">
      <h1 className="text-center">All Recipes</h1>
      <div className="row mt-4">
        {recipes.length === 0 ? (
          <p className="text-center">No recipes found. Add a recipe to get started!</p>
        ) : (
          recipes.map((recipe) => (
            <RecipeCard
              key={recipe._id}
              recipe={recipe}
              onEdit={() => {}}
              onDelete={() => {}}
            />
          ))
        )}
      </div>
    </div>
  );
};

export default RecipesPage;
